var app=angular.module("Mini-proyect",["ngRoute","firebase"]);app.config(function($routeProvider){$routeProvider.when("/home",{templateUrl:"partials/home-partial.html"}).when("/tablero",{templateUrl:"partials/tablero-main-partial.html",controller:"TableroCtrl"}).when("/agregarTablero",{templateUrl:"partials/agregar-tablero-partial.html",controller:"AgregarTableroCtrl"}).otherwise({redirectTo:"/home"})}),app.constant("FBURL","https://mini-trello-3354a.firebaseio.com/");;;app.controller("AgregarTableroCtrl",["$scope","$firebaseArray","FBURL",function($scope,$firebaseArray,FBURL){function val_data(){if(null!=$scope.tnombre&&null!=$scope.tdesc)return $scope.tnombre.length<=50&&$scope.tdesc.length<=50}$scope.tsubmit=function(){var root=new Firebase(FBURL),ref=root.child("/tableros");if(val_data()){var new_ref=ref.push(),id=new_ref.key();new_ref.set({nombre:$scope.tnombre,desc:$scope.tdesc,id_lista:id}),alert("Creado")}else alert("error")}}]);;;app.controller("TableroCtrl",["$scope","$firebaseArray","FBURL",function($scope,$firebaseArray,FBURL){var root=new Firebase(FBURL),ref=root.child("/tableros");$scope.tableros=$firebaseArray(ref)}]);